class Form extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          isGoing: true,
          numberOfGuests: 2
        };
      }
    
      handleInputChange(event) {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;
    
        this.setState({
          [name]: value
        });
      }
    
      handleSubmit(event) {
        alert('A name was submitted: ' + this.state.value);
        console.log("It Works");
        event.preventDefault();
      }
    
      render() {
        return (
          <form onSubmit={this.handleSubmit}>
            <label>
              Name:
              <input type="text" value={this.state.value} onChange={this.handleChange} />
            </label>
            <input type="submit" value="Submit" />
          </form>
        );
      }
    render() {
        return (
            <div className="card-body">
                <form onSubmit={this.handleSubmit}>
                    <p className="h4 text-center py-4">Decentralize Now</p>
                    <div className="md-form ">
                        <input type="text" name="fullName" className="form-control" value={this.state.value} onChange={this.handleChange}/>
                        <i className="material-icons mr-2 align-middle">account_box</i>
                        <label className="font-weight-light"> Your name</label>
                    </div>
                    <div className="md-form">
                        <input type="text" name="projectName" className="form-control" value={this.state.value} onChange={this.handleChange}/>
                        <i className="material-icons mr-2 align-middle">business</i>
                        <label className="font-weight-light"> Your project</label>
                    </div>
                    <div className="md-form">
                        <input type="email" name="email" className="form-control" value={this.state.value} onChange={this.handleChange}/>
                        <i className="material-icons mr-2 align-middle">email</i>
                        <label className="font-weight-light"> Your email</label>
                    </div>
                    <div className="text-center pb-4 mt-3">
                        <button className="btn btn-primary" type="submit">Stay Updated</button>
                        <small name="emailHelp" className="form-text text-muted">We'll never share your email.</small>
                    </div>
                </form>
            </div>
        );
    }
}
export default Form;
